import React from 'react';

class AddMovie extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }


  handleInputChange(event) {
    this.setState({ title: event.target.value });
  }

  render() {
    const { subtitle, title, imagePath, storyline, rating, genre } = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <label data-testid="title-input-label">
        Título
          <input
            data-testid="title-input"
            type="text"
            value={title}
            onChange={this.handleInputChange}
          />
        </label>
        <label data-testid="subtitle-input-label">
        Subtítulo
          <input data-testid="subtitle-input" type="text" value={subtitle} onChange={this.handleInputChange} />
        </label>
        <label data-testid="image-input-label">
        Imagem
          <input data-testid="image-input" type="text" value={imagePath} onChange={this.handleInputChange} />
        </label>
        <label data-testid="storyline-input-label">
        Sinopse
          <textarea data-testid="storyline-input" value={storyline} onChange={this.handleInputChange} />
        </label>
        <label data-testid="rating-input-label">
        Avaliação
          <input data-testid="rating-input" type="number" value={rating} onChange={this.handleInputChange} />
        </label>
        <label data-testid="genre-input-label">
        Gênero
          <select data-testid="genre-input" value={genre} onChange={this.handleInputChange}>
            <option value="action">Ação</option>
            <option value="comedy">Comédia</option>
            <option value="thriller">Suspense</option>
          </select>
        </label>

      </form>
    );
  }
}

export default AddMovie;
