import React from 'react';

class AddMovie extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }


  handleInputChange(event) {
    this.setState({ title: event.target.value });
  }

  render() {
    const { subtitle, title, imagePath, storyline, rating, genre } = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
        Título
          <input
            type="text"
            value={title}
            onChange={this.handleInputChange}
          />
        </label>
        <label>
        Subtítulo
          <input type="text" value={subtitle} onChange={this.handleInputChange} />
        </label>
        <label>
        Imagem
          <input type="text" value={imagePath} onChange={this.handleInputChange} />
        </label>
        <label>
        Sinopse
          <textarea value={storyline} onChange={this.handleInputChange} />
        </label>
        <label>
        Avaliação
          <input type="text" value={subtitle} onChange={this.handleInputChange} />
        </label>
        <label>
        Gênero
          <select value={genre} onChange={this.handleInputChange}>
            <option value="action">Laranja</option>
            <option value="comedy">Limão</option>
            <option value="thriller">Coco</option>
          </select>
        </label>

      </form>
    );
  }
}

export default AddMovie;
